        footer {
            background-color: #f1f1f1;
            padding: 20px 0;
            bottom: 0;
            width: 100%;
            text-align: center;
        }

        .footer-content {
            display: flex;
            justify-content: space-between;
            align-items: center;
            padding: 0 20px;
            flex-wrap: wrap;
        }

        .footer-links a {
            margin: 0 10px;
            text-decoration: none;
            color: #000;
            font-size: small;
        }

        .footer-links a:hover {
            text-decoration: underline;
        }

        .footer-info {
            display: flex;
            align-items: center;
            justify-content: space-between;
        }

        .footer-info p {
            margin: 0;
            margin-right: 10px;
        }

        .dropdown-menu {
            display: none;
        }

        @media(max-width : 750px) {
            .footer-content {
                display: block;
            }

            .footer-info {
                display: block;
                font-size: 10px;
            }

            .footer-info p {
                margin: 10px;
                /* margin-right: 10px; */
            }

            footer-links a {
                font-size: 10px;
            }

        }

